$(document).ready(function(){$("#gpc a").click(function(){$(this).parent().next().toggle();$(this).hasClass("c_detail")?$(this).removeClass("c_detail"):$(this).addClass("c_detail");});$(".opendetail a").click(function(){var li=$(this).parent();var issueName=$(this).attr("issueName");if(issueName){getDataList(li,issueName);}});});var getDataList=function(li,issueName){var data={};data.method="GetOpenAffiche";data.issueName=issueName;ajaxProcess("?",data,function(oRet){if(oRet.Error===""){if(!oRet.UsePool){var result=oRet.Result;if(result){if(li.next().is("li")){var html=[];html.push("<table class='t_sale'><tr><td>本期销量</br><i class='red'>{0}</i>元</td><td>奖池滚存</br><i class='red'>{1}</i>元</td></tr></table>".format(result.SumSales,result.PrizePool));if(result.T_WinType){html.push("<table class='t_award'><tr><th>奖项</th><th>中奖注数</th><th>单注奖金(元)</th></tr>");$(result.T_WinType).each(function(){html.push("<tr><td>{Name}</td><td>{WinCount}</td><td>{WinMoney}</td></tr>".format(this));});html.push("</table>");}$(".opendetail table:visible").hide();li.after(html.join(""));}else{if(li.next().is("table:visible")){$(".opendetail table:visible").hide();}else{$(".opendetail table:visible").hide();li.next().toggle().next().toggle();}}}}}else{alert(oRet.Error);}},function(err){console.log(err);alert(err);});}